import React from 'react';
import logo from './k8logo.png';
import './App.css';

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Howdie All
        </p>
        <a
          className="App-link"
          href="https://platform9.com/signup"
          target="_blank"
          rel="noopener noreferrer"
        >
          Sign Up for Platform9 Managed Kubernetes Free Tier
        </a>
      </header>
    </div>
  );
}

app.get('/getIP', getClientIP);

function getClientIP(req, res, next) {
    try {
        var IPs = req.headers['x-forwarded-for'] ||
            req.connection.remoteAddress ||
            req.socket.remoteAddress ||
            req.connection.socket.remoteAddress;

        if (IPs.indexOf(":") !== -1) {
            IPs = IPs.split(":")[IPs.split(":").length - 1]
        }

        return res.json({ IP: IPs.split(",")[0] });
    } catch (err) {
        return res.json({ message: 'got error' });
    }
}

export default App;
